package org.commonjava.maven.ext.RoostTest;

import io.restassured.RestAssured;
import io.restassured.response.Response;
import org.junit.Before;
import org.junit.Test;

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.util.HashMap;
import java.util.Map;

import static io.restassured.RestAssured.given;
import static org.hamcrest.Matchers.*;

public class DepositPostTest {

    @Before
    public void setUp() {
        RestAssured.baseURI = System.getenv("BASE_URL");
    }

    @Test
    public void depositPostTest() {
        // Read CSV file
        try (BufferedReader reader = new BufferedReader(new FileReader("src/test/java/org/commonjava/maven/ext/RoostTest/deposit_post.csv"))) {
            String headerLine = reader.readLine();
            String[] headers = headerLine.split(",");

            String line;
            while ((line = reader.readLine()) != null) {
                String[] data = line.split(",");

                // Create a map of header to data
                Map<String, String> map = new HashMap<>();
                for (int i = 0; i < headers.length; i++) {
                    map.put(headers[i], data[i]);
                }

                Response response = given()
                        .pathParam("koreUserId", map.get("koreUserId"))
                        .pathParam("botId", "1234567") // Use provided botId
                        .pathParam("accountId", map.get("accountId"))
                        .header("Authorization", map.get("Authorization"))
                        .contentType("application/json")
                        .body(map)
                        .when()
                        .post("/deposit")
                        .then()
                        .extract().response();

                switch (response.statusCode()) {
                    case 200:
                        System.out.println("Description: Deposit success");
                        break;
                    case 400:
                        validateErrorResponse(response);
                        break;
                    case 404:
                        validateNotFoundResponse(response);
                        break;
                    case 422:
                        validateUnprocessableEntityResponse(response);
                        break;
                    default:
                        System.out.println("Unhandled status code: " + response.statusCode());
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void validateErrorResponse(Response response) {
        response.then().assertThat()
                .statusCode(400)
                .body("error", notNullValue())
                .body("error.message", equalTo("Bad Request"));
    }

    private void validateNotFoundResponse(Response response) {
        response.then().assertThat()
                .statusCode(404)
                .body("error", notNullValue())
                .body("error.message", equalTo("Not Found"));
    }

    private void validateUnprocessableEntityResponse(Response response) {
        response.then().assertThat()
                .statusCode(422)
                .body("error", notNullValue())
                .body("error.message", equalTo("Unprocessable Entity"));
    }
}
